import React from 'react';
import './App.css';

import ProgressBar from './ProgressBar';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      startedTime: null, // 開始時刻
      nowTime: null, // 現在時刻
      setSeconds: 10, // 設定時間(秒単位)
      elapsedTime: 0, // 経過時間(秒単位)
      setIntervalId: null, // setIntervalのid
    }

    this.countdown = this.countdown.bind(this);
    this.onStart = this.onStart.bind(this);
    this.onStop = this.onStop.bind(this);
    this.onCancel = this.onCancel.bind(this);
  }

  countdown(){
    const { setIntervalId, startedTime, setSeconds } = this.state;
    const nowTime = new Date();
    const elapsedTime = Math.floor((nowTime - startedTime) / 1000);

    const time = startedTime ? setSeconds - elapsedTime : setSeconds;

    if(time === 0){
      clearInterval(setIntervalId);
      alert('時間になりました！');
    }else{
      this.setState({elapsedTime: elapsedTime});
    }
  }

  onStart(){
    const id = setInterval(this.countdown,1000);
    const date = new Date();
    this.setState({
      startedTime: date,
      setIntervalId: id,
    })
  }

  onStop(elapsedTime){
    const { setIntervalId } = this.state;
    const date = new Date();
    clearInterval(setIntervalId);
    this.setState({
      startedTime: date,
      elapsedTime: elapsedTime,
      setIntervalId: null
    })
  }

  onCancel(){
    const { setIntervalId } = this.state;
    clearInterval(setIntervalId);

    this.setState({
      startedTime: null,
      elapsedTime: 0,
      setIntervalId: null,
    })
  }

  render(){
    const { setIntervalId, startedTime, setSeconds, elapsedTime } = this.state;
    const progress = startedTime ?  100 - (elapsedTime / setSeconds * 100) : 100; 
    const time = startedTime ? setSeconds - elapsedTime : setSeconds;
    const text = String('00' + Math.floor(time / 3600)).slice(-2) + ':' + String('00' + Math.floor(time / 60) % 60).slice(-2) + ':' + String('0' + time % 60).slice(-2); 

    return (
      <div className="App">
        <ProgressBar 
          stroke={5}
          radius={200}
          progress={progress}
          text={text}
        />
        <div>
          <button 
            style={{marginRight: '120px'}} 
            className={'button'+(setIntervalId ? '' : ' button-disabled')} 
            type='button' 
            disabled={setIntervalId ? false : true}
            onClick={this.onCancel}
            >
              キャンセル
          </button>
          <button 
            className={'button'+(setSeconds === 0  ? ' button-disabled' : '')} 
            type='button'
            onClick={setIntervalId ? ()=>{this.onStop(time)} : this.onStart}
            disabled={setSeconds === 0}
          >
            {setIntervalId ? '停止' : '開始'}
          </button>
        </div>
      </div>
    );
  }
}

export default App;
