import React from 'react';
import './App.css';

import ProgressBar from './ProgressBar';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      isStarted: false,
      progress: 100,
      setTime: '00:00:00',
      remainingTime: '0:00:00',
      isPaused: true,
    }

    this.countdown = this.countdown.bind(this);
  }

  countdown(){
    const { isPaused } = this.state;  
