    }
    this.setState({remainingTime: ''});
  }

  onStart(){
    setInterval(this.countdown,500);
  }

  onStop(){
    this.setState({isPaused: true});
  }

  render(){
    const { isStarted, progress } = this.state;

    return (
      <div className="App">
        <ProgressBar stroke={5} radius={200} progress={progress} text={'3:53:00'}/>
        <div>
          <button 
            style={{marginRight: '120px'}} 
            className={'button'+(isStarted ? '' : ' button-disabled')} 
            type='button' 
            disabled={isStarted ? false : true}>
              キャンセル
          </button>
          <button 
            className='button' 
            type='button'
          >
            {isStarted ? '停止' : '開始'}
          </button>
        </div>
      </div>
    );
  }
}

export default App;
