import React from 'react';
import './App.css';

import ProgressBar from './ProgressBar';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      isStarted: false,
      progress: 100,
    }
  }
  render(){
    const { isStarted, progress } = this.state;

    return (
      <div className="App">
        <ProgressBar stroke={5} radius={200} progress={progress} text={'3:53:00'}/>
        <button className='button' type='button' disabled={isStarted ? false : true}>キャンセル</button>
        <button className='button' type='button'>{isStarted ? '停止' : '開始'}</button>
        }
      </div>
    );
  }
}

export default App;
