import React from 'react';
import './App.css';

import ProgressBar from './ProgressBar';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      startedTime: null, // 開始時刻
      nowTime: null, // 現在時刻
      setMinutes: 0, // 設定時間(分単位)
      setIntervalId: null, // setIntervalのid
    }

    this.countdown = this.countdown.bind(this);
    this.onStart = this.onStart.bind(this);
    this.onStop = this.onStop.bind(this);
  }

  countdown(){
    this.setState({nowTime: Date()});
  }

  onStart(){
    const id = setInterval(this.countdown,1000);
    this.setState({
      startedTime: Date(), 
      setIntervalId: id,
    })
  }

  onStop(){
    const { setIntervalId } = this.state;
    clearInterval(setIntervalId);
    this.setState({
      nowTime: Date(), 
      setIntervalId: null
    })
  }

  onCancel(){
    const { setIntervalId } = this.state;
    clearInterval(setIntervalId);

    this.setState({
      startedTime: null,
      nowTime: null,
      setIntervalId: null,
    })
  }

  render(){
    const { setIntervalId, nowTime, startedTime, setMinutes } = this.state;
    const elapsedTime = nowTime - startedTime;
    const progress = startedTime ?  elapsedTime / setMinutes : 100; 
    const time = startedTime ? setMinutes - elapsedTime : setMinutes;
    const text = String('0' + time / 3600).substr(-1,2) + ':' + String('0' + time / 60).substr(-1,2) + ':' + String('0' + time % 3600).substr(-1,2); 

    return (
      <div className="App">
        <ProgressBar 
          stroke={5}
          radius={200}
          progress={progress || 100}
          text={text}
        />
        <div>
          <button 
            style={{marginRight: '120px'}} 
            className={'button'+(setIntervalId ? '' : ' button-disabled')} 
            type='button' 
            disabled={setIntervalId ? false : true}>
              キャンセル
          </button>
          <button 
            className='button' 
            type='button'
            onClick={setIntervalId ? this.onStop : this.onStart}
          >
            {setIntervalId ? '停止' : '開始'}
          </button>
        </div>
      </div>
    );
  }
}

export default App;
