import React from 'react';
import './App.css';

import ProgressBar from './ProgressBar';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      isStarted: false,
    }
  }
  render(){
    const { isStarted } = this.state;
    return (
      <div className="App">
        <ProgressBar stroke={2} radius={200} progress={100}/>
        <button className='button' type='button'>キャンセル</button>
        {isStarted
          ? <button type='button'>開始</button>
          : <button type='button'>停止</button>
        }
      </div>
    );
  }
}

export default App;
